mapper.select:
SELECT b.name as businessLineName, b.id as businessLineId, a.description, c.name as devEmployeeName, c.id as devEmployeeId, a.due_date, a.id, a.name, a.priority, a.remark, d.name as requirementName, d.id as requirementId, e.name as sprintName, e.id as sprintId, a.status, a.story, f.workload as workload FROM tbl_sms_task_info a LEFT JOIN tbl_sms_businessline_info b ON a.businessLine_id = b.id LEFT JOIN tbl_sms_employee_info c ON a.dev_employee_id = c.id LEFT JOIN tbl_sms_requirement_info d ON a.requirement_id = d.id LEFT JOIN tbl_sms_sprint_info e ON a.sprint_id = e.id LEFT JOIN tbl_sms_task_workload_info f ON a.id = f.task_id
a.state = 0 AND b.state = 0
AND a.name like concat('%',#{name},'%')
AND a.businessLine_id = #{businessLineId}
AND a.requirement_id = #{requirementId}
AND a.sprint_id = #{sprintId}
AND a.dev_employee_id = #{devEmployeeId}
AND d.status = #{requirementStatus}
order by a.rec_upd_ts desc, a.businessLine_id asc
limit #{offset}, #{pageSize}

select
count(*)
from
(
select
*
from tbl_sms_task_info

    state = 0

        AND name like concat('%',#{name},'%')


        AND businessLine_id = #{businessLineId}


        AND requirement_id = #{requirementId}


        AND sprint_id = #{sprintId}


        AND dev_employee_id = #{devEmployeeId}


) t1

mapper.update:
UPDATE tbl_sms_task_info
SET status= #{status}
WHERE id = #{id}
  AND state = 0
